    private void btn1(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn1
        int n1 = Integer.parseInt(txt1.getText());
        int n2 = Integer.parseInt(txt2.getText());
        String div = String.valueOf(n1 / n2);
        lbldiv.setText(div);
        lblraiz.setText(String.valueOf((float)Math.sqrt(n1)));
    }//GEN-LAST:event_btn1

