    private void btnDataHora(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDataHora
        Date data = new Date();
        Locale brasil = new Locale("pt", "BR");
        DateFormat df2 = DateFormat.getDateTimeInstance(DateFormat.FULL, NORMAL, brasil);
        lblHoraData.setText(df2.format(data));
    }//GEN-LAST:event_btnDataHora

