    int soma (){
        int fat = (int) cont1.getValue();
        for (int c = fat-1 ; c > 1; --c){
            fat *= c;
        }
        return fat;
    }
    void format (){
        int fat = (int) cont1.getValue();
        String txt = "";
        for (int c = fat-1; c > 1; --c){
            txt += c + " x ";
        }
        fat = soma();
        lbl1.setText(cont1.getValue() + " x " + txt + "1 = " + fat);
    }

